mapper.select:
select  count(*)	 
from 	cart_tb
where 	m_id	=	#{m_id} 
	and pno		=	#{pno}

mapper.update:
UPDATE 	cart_tb
SET 	cart_count 	= cart_count + #{cart_count}
WHERE 	m_id 		= #{m_id}
	AND pno 		= #{pno}

UPDATE 	cart_tb
SET 	cart_count 	= #{cart_count}
WHERE 	m_id 		= #{m_id}
	AND pno 		= #{pno}
